export const fetchGoods = async () => {
    const response = await fetch('http://localhost:3012/goods', {method: 'POST'});
    const goods = await response.json();

    return goods;
};

export const tryLoginUser = async(login, password) => {
    let registrationData = {
        login: login,
        password: password
    };
    const userInfo = await fetch('http://localhost:3012/login', {
            method: 'POST',
            body: JSON.stringify(registrationData)
        });


    return userInfo;
};